  serialNumber: string;
  gasType: string;
  gasCategory: string;
  manufacturer: string;
  capacity: string;
  workingPressure: string;
  testPressure: string;
  testDate: string;
  standardCode: string;
  expiryDate: string;
  inspectionDate: string;
  locationType: string;
  rustLevel: string;
  hazardType: string;
  capPresent: string;
  labelCondition: string;
  remarks: string;
