  onAudioReady: (audioBuffer: AudioBuffer) => void
): Promise<{ success: boolean }> => {
  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

  // The 400 error 'model returned non-audio response' happens because instructions 
  // like "Return only the final narration text" trigger text output which is 
  // illegal when responseModalities is set to AUDIO.
  // We frame this as a direct 'Speak' command to ensure audio-only modality compliance.
  const prompt = `You are a strict multilingual narration generator.
The user has selected the narration language: ${languageName}.

CRITICAL INSTRUCTIONS:
1. Speak the entire narration ONLY in ${languageName}.
2. Do NOT use English unless the selected language is English.
3. Translate all headings, labels, and values into ${languageName}.
4. Convert serial number digits, cylinder IDs, and all numerical data into spoken words in ${languageName}.
5. Alphanumeric strings (Serial Numbers/IDs) MUST be read CHARACTER BY CHARACTER in ${languageName}.
6. The output must be 100% AUDIO in ${languageName}. Do NOT output any text commentary.
7. Use the correct script and phonetics for: ${languageName}.

REPORT DATA TO NARRATE:
${text}`;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash-preview-tts",
      contents: [{ parts: [{ text: prompt }] }],
      config: {
        responseModalities: [Modality.AUDIO],
        speechConfig: {
          voiceConfig: {
            prebuiltVoiceConfig: { voiceName: 'Kore' },
          },
        },
      },
    });

    const audioPart = response.candidates?.[0]?.content?.parts?.find(p => !!p.inlineData);
    const base64Audio = audioPart?.inlineData?.data;

    if (base64Audio) {
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)({ sampleRate: 24000 });
      const audioData = decode(base64Audio);
      const buffer = await decodeAudioData(audioData, audioContext, 24000, 1);
      onAudioReady(buffer);
      return { success: true };
